if(mode==1){
width=x1+5;
if(width<10) width=10;
height=y1+5;
if(height<10) height=10;
}
else{
x0+=x1-x2;
if(x0<0) x0=0;
y0+=y1-y2;
if(y0<0) y0=0;
}
