grp.drawRect( x0+1, y0+1, width-2, height-2);
grp.setPaintMode();

Graphics grp = cmp.getParent().getGraphics();
grp.setXORMode(Color.white);
grp.drawRect( x0+1, y0+1, width-2, height-2);
x1=x;
y1=y;
if(mode==1){
width=x1+5;
if(width<10) width=10;
height=y1+5;
if(height<10) height=10;
}
else{
x0+=x1-x2;
if(x0<0) x0=0;
y0+=y1-y2;
if(y0<0) y0=0;
}
x2=x1;
y2=y1;
grp.drawRect( x0+1, y0+1, width-2, height-2);
grp.setPaintMode();

Graphics grp = cmp.getParent().getGraphics();
grp.setXORMode(Color.white);
grp.drawRect( x0+1, y0+1, width-2, height-2);
grp.setPaintMode();
cmp.setBounds( x0, y0, width, height );
cmp.repaint();

