//ボタンをクリックするとClickピンに信号が出力される
inbutton b;//ボタンのインスタンス
class inbutton extends JButton implements ActionListener{
Xbutton parent;
inbutton(Xbutton prnt){
parent=prnt;
addActionListener(this);
setText(ButtonName);
}

public void actionPerformed(ActionEvent e){
parent.Click();
}
}

