void xread( int n ){
  int i;
  if( WorkFile != null ){
    try{

    if( n >= 0 ){
      WorkFile.seek( 4 + 3 * 8 * NumberOfStars * n );
      for( i = 0;i < NumberOfStars; i++){
        px[i] = WorkFile.readDouble();
      }
      for( i = 0;i < NumberOfStars; i++){
        py[i] = WorkFile.readDouble();
      }
      for( i = 0;i < NumberOfStars; i++){
        pz[i] = WorkFile.readDouble();
      }
    }

    } catch( IOException e ){}
  }
}
