void xwrite( int n ){
  int i;
  try{

  if( n == 0 ){
    WorkFile.seek(0);
    WorkFile.writeInt(NumberOfStars);
  }
  else{
    WorkFile.seek( 4 + 3 * 8 * NumberOfStars * n );
  }
  for( i = 0;i < NumberOfStars; i++){
    WorkFile.writeDouble(px[i]);
  }
  for( i = 0;i < NumberOfStars; i++){
    WorkFile.writeDouble(py[i]);
  }
  for( i = 0;i < NumberOfStars; i++){
    WorkFile.writeDouble(pz[i]);
  }

  } catch( IOException e ){}
}
